INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'optimization', 'Оптимизация', 'Поисковая оптимизация (англ. search engine optimization, SEO) — комплекс мер по внутренней и внешней оптимизации, для поднятия позиций сайта в результатах выдачи поисковых систем по определенным запросам пользователей', '', '', '', '', 90);
