INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'optimization', 'Оптимизация', 'Поисковая оптимизация (англ. search engine optimization, SEO) — комплекс мер по внутренней и внешней оптимизации, для поднятия позиций сайта в результатах выдачи поисковых систем по определенным запросам пользователей', '', '', '', '', 90);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'company', 'Компания', 'Компания — (company) Корпоративное предприятие, которое является юридическим лицом, в отличие от его участников; компания оперирует как самостоятельная единица, успеха которой добиваются все ее члены', '', '', '', '', 50);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'business', 'Бизнес', 'Бизнес - (англ, business - дело, предпринимательство) - инициативная экономическая деятельность, осуществляемая за счет собственных или заемных средств на свой риск и под свою ответственность, ставящая главными целями получение прибыли и развитие собственного дела', '', '', '', '', 0);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'e-commerce', 'Электронная коммерция', 'Электронная коммерция — это сфера экономики, которая включает в себя все финансовые и торговые транзакции, осуществляемые при помощи компьютерных сетей, и бизнес-процессы, связанные с проведением таких транзакций', '', '', '', '', 0);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'integration-business-apps', 'Интеграция бизнес-приложений', 'Бизнес-интеграция состоит в объединении полученной из приложений и источников данных информации с людьми и процессами', '', '', '', '', 0);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'outsourcing', 'Аутсорсинг', 'Аутсорсинг — передача организацией, на основании договора, определённых видов или функций производственной предпринимательской деятельности другой компании, действующей в нужной области', '', '', '', '', 90);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'workflow', 'Документооборот', 'Документооборот – это движение документов в организации с момента их создания или получения до завершения исполнения или отправки', '', '', '', '', 50);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'staff', 'Персонал', 'Персонал (от лат. persona - личность) - коллектив работников или совокупность лиц, осуществляющих трудовые функции на основе трудового договора или контракта', '', '', '', '', 0);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'automatic-control', 'Автоматизация', 'Автоматизация, это одно из направлений научно-технического прогресса, использующее саморегулирующие технические средства', '', '', '', '', 0);