INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'e-commerce', 'Электронная коммерция', 'Электронная коммерция — это сфера экономики, которая включает в себя все финансовые и торговые транзакции, осуществляемые при помощи компьютерных сетей, и бизнес-процессы, связанные с проведением таких транзакций', '', '', '', '', 0);
INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'integration-business-apps', 'Интеграция бизнес-приложений', 'Бизнес-интеграция состоит в объединении полученной из приложений и источников данных информации с людьми и процессами', '', '', '', '', 0);
