INSERT INTO {pref}_settings VALUES(NULL, '{mod}', 'adduse', 'user', 0, 'who_add', 'echo "<select name=\\"set[adduse]\\" class=\\"group-sel sw165\\" id=\\"acc\\">".    \r\n     "<option value=\\"all\\"".(($conf[''{mod}''][''adduse'']=="all") ? " selected" : "").">".$lang[''all_all'']."</option>\\n";\r\nif (in_array(''user'', $realmod)) {\r\necho "<option value=\\"user\\"".(($conf[''{mod}''][''adduse'']=="user" && empty($conf[''{mod}''][''groups''])) ? " selected" : "").">".$lang[''all_user_only'']."</option>\\n";    \r\necho (($conf[''user''][''groupact''] == "yes") ? "<option value=\\"group\\"".(($conf[''{mod}''][''adduse''] == "user" && !empty($conf[''{mod}''][''groups'']))  ? " selected" : "").">".$lang[''all_groups_only'']."</option>\\n" : "");\r\necho "</select>\\n";\r\necho "<div class=\\"group\\" id=\\"group\\"".(($conf[''{mod}''][''adduse''] == "all" || $conf[''{mod}''][''adduse''] == "user" && empty($conf[''{mod}''][''groups''])) ? " style=\\"display: none;\\"" : "").">\\n";\r\nif ($conf[''user''][''groupact''] == "yes") {\r\n    $inqs = $db->query("SELECT * FROM ".$basepref."_user_group");\r\n    $group = Json::decode($conf[''{mod}''][''groups'']); \r\n    $group_out = "";\r\n    while ($items = $db->fetchrow($inqs)) {\r\n        $group_out.= "<input type=\\"checkbox\\" name=\\"group[".$items[''gid'']."]\\" value=\\"yes\\"".(isset($group[$items[''gid'']]) ? " checked" : "")."><span>".$items[''title'']."</span>,"; \r\n    }\r\n    echo chop($group_out,",");\r\n}\r\n}\r\necho "</div>\\n";', '$set[''groups''] = ($conf[''user''][''groupact''] == ''yes'' && $set[''adduse''] == ''group'' && is_array($group)) ? Json::encode($group) : '''';\r\n$set[''adduse''] = ($set[''adduse''] == ''user'' || $set[''adduse''] == ''group'') ? ''user'' : ''all'';');
