INSERT INTO {pref}_settings VALUES(NULL, '{mod}', 'comact', 'yes', 0, 'comments', 'echo "<select name=\\"set[comact]\\" class=\\"sw165\\">".\r\n"<option value=\\"yes\\"".(($conf[''{mod}''][''comact'']=="yes") ? " selected" : "").">".$lang[''included'']."</option>\\n".\r\n"<option value=\\"no\\"".(($conf[''{mod}''][''comact'']=="no") ? " selected" : "").">".$lang[''not_included'']."</option>\\n".\r\n"</select>";', '$set["comact"] = ($set["comact"] == "yes") ? "yes" : "no";');
INSERT INTO {pref}_settings VALUES(NULL, '{mod}', 'comwho', 'user', 0, 'comment_who', 'echo "<select name=\\"set[comwho]\\" class=\\"sw165\\">".\r\n     "<option value=\\"all\\"".(($conf[''{mod}''][''comwho'']=="all") ? " selected" : "").">".$lang[''all_all'']."</option>\\n";\r\nif (in_array(''user'', $realmod)) {\r\necho "<option value=\\"user\\"".(($conf[''{mod}''][''comwho'']=="user") ? " selected" : "").">".$lang[''all_user_only'']."</option>\\n";\r\n}\r\necho "</select>";', '$set["comwho"] = ($set["comwho"] == "user") ? "user" : "all";');
INSERT INTO {pref}_settings VALUES(NULL, '{mod}', 'comsmilie', 'no', 0, 'smilies', 'echo "<select name=\\"set[comsmilie]\\" class=\\"sw165\\">".\r\n"<option value=\\"yes\\"".(($conf[''{mod}''][''comsmilie'']=="yes") ? " selected" : "").">".$lang[''included'']."</option>\\n".\r\n"<option value=\\"no\\"".(($conf[''{mod}''][''comsmilie'']=="no") ? " selected" : "").">".$lang[''not_included'']."</option>\\n".\r\n"</select>";', '$set["comsmilie"] = ($set["comsmilie"] == "yes") ? "yes" : "no";');
INSERT INTO {pref}_settings VALUES(NULL, '{mod}', 'comeditor', 'yes', 0, 'comment_editor_tags', 'echo "<select name=\\"set[comeditor]\\" class=\\"sw165\\">".\r\n"<option value=\\"yes\\"".(($conf[''{mod}''][''comeditor'']=="yes") ? " selected" : "").">".$lang[''included'']."</option>\\n".\r\n"<option value=\\"no\\"".(($conf[''{mod}''][''comeditor'']=="no") ? " selected" : "").">".$lang[''not_included'']."</option>\\n".\r\n"</select>";', '$set["comeditor"] = ($set["comeditor"] == "yes") ? "yes" : "no";');
INSERT INTO {pref}_settings VALUES(NULL, '{mod}', 'indcol', '1', 1, 'who_col_all', 'echo "<input type=\\"text\\" name=\\"set[indcol]\\" value=\\"".$conf[''{mod}''][''indcol'']."\\" size=\\"25\\" maxlength=\\"2\\" required=\\"required\\">";', '$set["indcol"] = preparse($set["indcol"],THIS_INT);');
